plugins.nginx_config:
location /api/file/cloud/name {

    default_type application/json;

    return 200 '{"ret": 1, "msg": "success", "data": {"cloud_provider": "aliyun"}}';

}



location /api/file/cloud/status {
    proxy_pass http://{{.ContainerName}}:{{.Port}}/api/file/content/status;

    proxy_set_header Host $http_host;
    proxy_set_header X-Real-IP $remote_addr;
    proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    proxy_set_header X-Forwarded-Proto $scheme;
}



location /api/file/cloud/upload {
