plugins.docker_compose:
version: '3'

services:
  doocloudisk:
    image: xxyijixx/doocloudisk:0.0.3
    restart: unless-stopped
    container_name: ${CONTAINER_NAME}
    networks:
      ${DOOTASK_NETWORK_NAME}:
        ipv4_address: ${IP_ADDRESS}
    environment:
      # 基础配置
      - PORT=8888
      - NGINX_URL=${APP_IPPR}.3
      - LOCAL_DOWNLOAD_DIR=/app/downloads
      - CLOUD_PROVIDER=${CLOUD_PROVIDER}  # 可选: aliyun, qiniu, tencent
      - DB_DSN=dootask:123456@tcp(${APP_IPPR}.5:3306)/dootask?charset=utf8&parseTime=True&loc=Local

      # 阿里云 OSS 配置
      - OSS_REGION=${OSS_REGION}
      - OSS_ENDPOINT=${OSS_ENDPOINT}
      - OSS_BUCKET=${OSS_BUCKET}
      - OSS_ACCESS_KEY_ID=${OSS_ACCESS_KEY_ID}
      - OSS_ACCESS_KEY_SECRET=${OSS_ACCESS_KEY_SECRET}

      # 腾讯云 COS 配置
      - COS_REGION=${COS_REGION}
      - COS_BUCKET=${COS_BUCKET}
      - COS_SECRETID=${COS_SECRETID}
      - COS_SECRETKEY=${COS_SECRETKEY}

      # 七牛云 Kodo 配置
      - QINIU_ACCESSKEY=${QINIU_ACCESSKEY}
      - QINIU_SECRETKEY=${QINIU_SECRETKEY}
      - QINIU_BUCKET=${QINIU_BUCKET}
      - QINIU_ENDPOINT=${QINIU_ENDPOINT}
      - QINIU_ZONE=${QINIU_ZONE}
    volumes:
      - ${DOOTASK_DIR}/docker/uploads:/app/uploads
    cpus: "${CPUS}"
    mem_limit: "${MEMORY_LIMIT}"
    labels:
      createdBy: "Apps"

networks:
  ${DOOTASK_NETWORK_NAME}:
    external: true

version: '3'

services:
  okr:
    image: "kuaifan/doookr:0.4.5"
    restart: unless-stopped
    container_name: ${CONTAINER_NAME}
    networks:
      ${DOOTASK_NETWORK_NAME}:
        ipv4_address: "${DOOTASK_APP_IPPR}.13"
    environment:
      # 基础配置
      - TZ=${TIMEZONE:-RPC}
      - DOO_TASK_URL=http://${APP_IPPR}.3     
      - MYSQL_HOST=${DOOTASK_DB_HOST}
      - MYSQL_PORT=${DOOTASK_DB_PORT}
      - MYSQL_DBNAME=${DOOTASK_DB_DATABASE}
      - MYSQL_USERNAME=${DOOTASK_DB_USERNAME}
      - MYSQL_PASSWORD=${DOOTASK_DB_PASSWORD}
      - MYSQL_PREFIX=${DOOTASK_DB_PREFIX}
      - DEMO_DATA=true
      - KEY=${DOOTASK_APP_KEY}

    cpus: "${CPUS}"
    mem_limit: "${MEMORY_LIMIT}"
    labels:
      createdBy: "Apps"

networks:
  ${DOOTASK_NETWORK_NAME}:
    external: true

plugins.nginx_config:
location /minder/ {
        proxy_http_version 1.1;
        proxy_set_header Connection "";
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Real-PORT $remote_port;
        proxy_set_header X-Forwarded-Host $the_host;
        proxy_set_header X-Forwarded-Proto $the_scheme;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header Host $http_host;
        proxy_set_header Scheme $scheme;
        proxy_set_header Server-Protocol $server_protocol;
        proxy_set_header Server-Name $server_name;
        proxy_set_header Server-Addr $server_addr;
        proxy_set_header Server-Port $server_port;
        proxy_pass http://{{.ContainerName}}/;
    }

 location /drawio/webapp/ {
    proxy_http_version 1.1;
    proxy_set_header X-Real-IP $remote_addr;
    proxy_set_header X-Real-PORT $remote_port;
    proxy_set_header X-Forwarded-Host $the_host/drawio/webapp;
    proxy_set_header X-Forwarded-Proto $the_scheme;
    proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    proxy_set_header Host $http_host;
    proxy_set_header Scheme $scheme;
    proxy_set_header Server-Protocol $server_protocol;
    proxy_set_header Server-Name $server_name;
    proxy_set_header Server-Addr $server_addr;
    proxy_set_header Server-Port $server_port;
    proxy_set_header Upgrade $http_upgrade;
    proxy_set_header Connection $connection_upgrade;
    proxy_pass http://{{.ContainerName}}:8080/;
}

location /approve/ {
    proxy_pass http://approve/;
}
location /approve/api/ {
    auth_request /approveAuth;
    proxy_pass http://approve/api/;
}
location /approveAuth {
    internal;
    proxy_set_header Content-Type "application/json";
    proxy_set_header Content-Length $request_length;
    proxy_pass http://service/api/approve/verifyToken;
}

location /office/ {
    proxy_http_version 1.1;
    proxy_set_header X-Real-IP $remote_addr;
    proxy_set_header X-Real-PORT $remote_port;
    proxy_set_header X-Forwarded-Host $the_host/office;
    proxy_set_header X-Forwarded-Proto $the_scheme;
    proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    proxy_set_header Host $http_host;
    proxy_set_header Scheme $scheme;
    proxy_set_header Server-Protocol $server_protocol;
    proxy_set_header Server-Name $server_name;
    proxy_set_header Server-Addr $server_addr;
    proxy_set_header Server-Port $server_port;
    proxy_set_header Upgrade $http_upgrade;
    proxy_set_header Connection $connection_upgrade;
    proxy_read_timeout 3600s;
    proxy_send_timeout 3600s;
    proxy_connect_timeout 3600s;
    proxy_pass http://{{.ContainerName}}/;
}

location /ai/ {
    proxy_http_version 1.1;
    proxy_buffering off;
    proxy_cache off;
    proxy_read_timeout 300s;
    proxy_send_timeout 300s;
    proxy_set_header Scheme $scheme;
    proxy_set_header Upgrade $http_upgrade;
    proxy_set_header Connection $connection_upgrade;
    proxy_pass http://ai:5001/;
}
